import React, { useState } from 'react';
import './Sidebar.css'; // Import CSS file for styling
import logo from '../images/logo.jpg'; // Ensure the path to your image is correct
import HelpCenter from '../helpCenter/HelpCenter'; // Adjust path based on actual file location
import dashboard from '../icons/dashboard.svg';
import mySkills from '../icons/mySkills.svg';
import myTraining from '../icons/myTraining.svg';
import knowledgeHub from '../icons/knowledgeHub.svg';
import growthPlan from '../icons/growthPlan.svg';

function Sidebar() {
  const [activePage, setActivePage] = useState('/'); // Default to Dashboard

  const handleClick = (path) => {
    setActivePage(path);
  };

  return (
    <aside className="sidebar">
      <div className="logo-container">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      <ul className="category-list">
        <li>
          <button 
            className={`sidebar-button ${activePage === '/' ? 'active' : ''}`}
            onClick={() => handleClick('/')}
          >
            <img src={dashboard} alt="Dashboard Icon" className="icon" />
            <span className="button-text">Dashboard</span>
          </button>
        </li>
        <li>
          <button 
            className={`sidebar-button ${activePage === '/my-skills' ? 'active' : ''}`}
            onClick={() => handleClick('/my-skills')}
          >
            <img src={mySkills} alt="My Skills Icon" className="icon" />
            <span className="button-text">My Skills</span>
          </button>
        </li>
        <li>
          <button 
            className={`sidebar-button ${activePage === '/my-training' ? 'active' : ''}`}
            onClick={() => handleClick('/my-training')}
          >
            <img src={myTraining} alt="My Training Icon" className="icon" />
            <span className="button-text">My Training</span>
          </button>
        </li>
        <li>
          <button 
            className={`sidebar-button ${activePage === '/knowledge-hub' ? 'active' : ''}`}
            onClick={() => handleClick('/knowledge-hub')}
          >
            <img src={knowledgeHub} alt="Knowledge Hub Icon" className="icon" />
            <span className="button-text">Knowledge Hub</span>
          </button>
        </li>
        <li>
          <button 
            className={`sidebar-button ${activePage === '/growth-plan' ? 'active' : ''}`}
            onClick={() => handleClick('/growth-plan')}
          >
            <img src={growthPlan} alt="Growth Plan Icon" className="icon" />
            <span className="button-text">Growth Plan</span>
          </button>
        </li>
      </ul>
      <div className="help">
        <HelpCenter />
      </div>
    </aside>
  );
}

export default Sidebar;
