import React from 'react';
import './MySkills.css'; // Make sure to link this CSS file for styles
import { Doughnut } from 'react-chartjs-2'; // Import Doughnut chart
import Chart from 'chart.js/auto'; // Import Chart.js

// Plugin to add number in the center of the donut chart and shadow effect
const centerTextPlugin = (filledSegments) => ({
  id: 'centerTextPlugin',
  beforeDraw: (chart) => {
    const { ctx, chartArea: { width, height } } = chart;

    // Draw shadow effect around the chart
    ctx.save();
    ctx.shadowColor = 'rgba(0, 0, 0, 0.10)'; // Slightly lighter shadow color
    ctx.shadowBlur = 10; // Reduced blur for the shadow
    ctx.shadowOffsetX = 2; // Horizontal shadow offset
    ctx.shadowOffsetY = 2; // Vertical shadow offset

    // Draw each segment with the shadow
    chart.getDatasetMeta(0).data.forEach((segment) => {
      segment.draw(ctx);
    });

    ctx.restore();

    // Draw number in the center of the donut chart
    ctx.font = '30px Lexend Deca'; // Set the font size to 30px
    ctx.fillStyle = '#000'; // Text color
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    const text = `${filledSegments}`; // Number of colored segments
    ctx.fillText(text, width / 2, height / 2 + 5); // Adjust position of the text to be centered
  }
});

const MySkills = () => {
  // Define data and number of filled segments for each donut chart
  const charts = [
    { data: [6, 4], filled: 6, backgroundColor: ['#FDECD8', '#ddd'] }, // 6 colored segments
    { data: [8, 2], filled: 8, backgroundColor: ['#97DDA8', '#ddd'] }, // 8 colored segments
    { data: [5, 5], filled: 5, backgroundColor: ['#F6A3B6', '#ddd'] }, // 5 colored segments
    { data: [9, 1], filled: 9, backgroundColor: ['#97DDA8', '#ddd'] }, // 9 colored segments
    { data: [3, 7], filled: 3, backgroundColor: ['#F6A3B6', '#ddd'] }, // 3 colored segments
    { data: [7, 3], filled: 7, backgroundColor: ['#97DDA8', '#ddd'] }  // 7 colored segments
  ];

  return (
    <div className="skills-container">
      <div className="skills-text1">
        <h2>Role:</h2>
        <p>Account Executive</p>
      </div>
      <div className="skills-text2">
        <h2>Level:</h2>
        <p>Intermediate</p>
      </div>

      {/* Map through each chart to generate the corresponding card */}
      {charts.map((chart, index) => (
        <div key={index} className={`box-card${index + 1}`}>
          <h3></h3>
          <div className="chart-container">
            <Doughnut
              data={{
                labels: [], // Empty labels
                datasets: [
                  {
                    data: chart.data,
                    backgroundColor: chart.backgroundColor,
                    borderColor: ['white'],
                    borderWidth: 4
                  }
                ]
              }}
              plugins={[centerTextPlugin(chart.filled)]} // Pass the filled number dynamically
              options={{
                cutout: '65%',
                plugins: {
                  legend: {
                    position: 'right',
                    labels: {
                      boxWidth: 20,
                      padding: 20,
                      font: {
                        size: 14
                      }
                    },
                    padding: {
                      right: 100
                    }
                  }
                }
              }}
            />
          </div>
        </div>
      ))}

      <footer className="footer">
        <p>Â© Copyrights Protected by Vekla</p>
      </footer>
    </div>
  );
};

export default MySkills;
