import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/layouts/Header';
import Sidebar from './components/layouts/Sidebar';
import Dashboard from './pages/Dashboard/Dashboard';
import MyTraining from './pages/MyTraining/MyTraining';
import MySkills from './pages/MySkills/MySkills';
import GrowthPlan from './pages/GrowthPlan/GrowthPlan';
import KnowledgeHub from './pages/KnowledgeHub/KnowledgeHub';
import ContactUs from './pages/ContactUs/ContactUs';
import './index.css';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Close sidebar
  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <Router>
      <div className="app">
        {/* Pass the toggleSidebar function to Header */}
        <Header toggleSidebar={toggleSidebar} />
        <div className="main">
          {/* Pass isOpen and closeSidebar as props to Sidebar */}
          <Sidebar isOpen={sidebarOpen} closeSidebar={closeSidebar} />
          <div className="content">
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/my-training" element={<MyTraining />} />
              <Route path="/my-skills" element={<MySkills />} />
              <Route path="/growth-plan" element={<GrowthPlan />} />
              <Route path="/knowledge-hub" element={<KnowledgeHub />} />
              <Route path="/contact-us" element={<ContactUs />} />
            </Routes>
          </div>
        </div>
        {/* Add an overlay when the sidebar is open, clicking it closes the sidebar */}
        {sidebarOpen && <div className="sidebar-overlay" onClick={closeSidebar}></div>}
      </div>
    </Router>
  );
}

export default App;
