import React from 'react';
import './MySkills.css'; // Make sure to link this CSS file for styles
import { Doughnut } from 'react-chartjs-2'; // Import Doughnut chart

// Data for each donut chart
const chartData1 = {
  labels: [],
  datasets: [
    {
      data: [10, 10, 10, 10, 10, 10, 10, 10, 10, 10], // Example data
      backgroundColor: ['#97DDA8', '#F6A3B6', '#FDECD8'], // Colors for the segments
      borderColor: ['white'],
      borderWidth: 2,
    },
  ],
};

const chartData2 = {
  labels: [],
  datasets: [
    {
      data: [10, 10, 10, 10, 10, 10, 10, 10, 10, 10],
      backgroundColor: ['#FFC107', '#007BFF', '#28A745'],
      borderColor: ['white'],
      borderWidth: 2,
    },
  ],
};

const chartData3 = {
  labels: [],
  datasets: [
    {
      data: [10, 10, 10, 10, 10, 10, 10, 10, 10, 10],
      backgroundColor: ['#007BFF', '#28A745'],
      borderColor: ['white'],
      borderWidth: 4,
    },
  ],
};

const centerTextPlugin = {
  id: 'centerTextPlugin',
  beforeDraw: (chart) => {
    const { ctx, chartArea: { width, height } } = chart;

    // Draw shadow effect around the chart
    ctx.save();
    ctx.shadowColor = 'rgba(0, 0, 0, 0.10)'; // Slightly lighter shadow color
    ctx.shadowBlur = 10; // Reduced blur for the shadow
    ctx.shadowOffsetX = 2; // Horizontal shadow offset
    ctx.shadowOffsetY = 2; // Vertical shadow offset

    // Draw each segment with the shadow
    chart.getDatasetMeta(0).data.forEach((segment) => {
      segment.draw(ctx);
    });

    ctx.restore();

    // Draw number in the center of the donut chart
    ctx.font = '30px Lexend Deca'; // Set the font size to 30px
    ctx.fillStyle = '#000'; // Text color
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    const text = '23'; // Example number of hours
    ctx.fillText(text, width / 2, height / 2 + 5); // Adjust position of the text to be centered
  }
};

<Doughnut
  data={chartData}
  plugins={[centerTextPlugin]}
  options={{
    cutout: '65%', // Adjust this value to control donut thickness
    plugins: {
      legend: {
        position: 'right', // Keep labels on the right side
        labels: {
          boxWidth: 20, // Adjust the size of the color box for the labels
          padding: 20, // Adjust padding between legend and chart
          font: {
            size: 14, // Adjust font size for legend labels
          },
        },
        // Increase the padding to move the labels further right
        padding: {
          right: 100, // Add more padding to push labels further from the chart
        }
      }
    }
  }}
/>

// Add additional data for other charts as needed...

const MySkills = () => {
  return (
    <div className="skills-container">
      <div className="skills-text1">
        <h2>Role:</h2>
        <p>Account Executive</p>
      </div>
      <div className="skills-text2">
        <h2>Level:</h2>
        <p>Intermediate</p>
      </div>
      <div className="box-card1">
        <h3></h3>
        <Doughnut data={chartData1} options={{ cutout: '75' }} />
      </div>
      <div className="box-card2">
        <h3></h3>
        <Doughnut data={chartData2} options={{ cutout: '30%' }} />
      </div>
      {/* Repeat for additional cards with different chart data */}
      <div className="box-card3">
        {/* Add another Doughnut chart here */}
      </div>
      <div className="box-card4">
        {/* Add another Doughnut chart here */}
      </div>
      <div className="box-card5">
        {/* Add another Doughnut chart here */}
      </div>
      <div className="box-card6">
        {/* Add another Doughnut chart here */}
      </div>
      <footer className="footer">
        <p>Â© Copyrights Protected by Vekla</p>
      </footer>
    </div>
  );
};

export default MySkills;
